import logo from './logo.svg';
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [getUser, setUser] = useState('');

  const clickButton = () => {
    axios.post("/api/test1", { user: "ddd" })
      .then((res) => {
        setUser(res.data)
      })
      .catch((err) => {
        console.log(err)
      })
  }

  const QnAContent = ({ row }) => {
    return (
      <>
        <div className="questionContainer" style={qnaContent_container}>
          <p style={qnaContent_container__p}>
            Q.
            {row.type !== 1 && row.taskTitle && (
              <span style={{ fontSize: '12px', padding: '0px 5px' }}>
                <Tag color="blue">프로젝트</Tag>
                <Text>{row.taskTitle}</Text>
              </span>
            )}
          </p>
          <div>
            {/* 등록된 이미지가 있는 경우 렌더링 */}
            {row.images != [] &&
              row.images.map((image, key) => {
                var imageLink = image;
                return <Image width={100} height={100} src={imageLink} />;
              })}
          </div>
          {/* 질문 내용 */}
          <pre
            className="questionContents"
            style={qnaContent_container_contents}
          >
            {row.question}
          </pre>
          {/* 문의내역 삭제 */}
          <div className="button" style={qnaContent_container_bottom}>
            <Space>
              <Button htmlType="button" onClick={onHandleModal}>
                삭제
              </Button>
            </Space>
          </div>
        </div>
        {/* 답변이 있을 경우 답변 렌더링*/}
        {row.state === 1 && (
          <>
            {/* 문의내역과 답변 구분선 */}
            <div
              style={{
                borderTop: '1px solid rgb(235, 236, 238)',
                marginBottom: '12px',
              }}
            ></div>
            {/* 답변 */}
            <div className="answerContainer" style={qnaContent_container}>
              <p style={qnaContent_container__p}>A.</p>
              <pre className="answer" style={qnaContent_container_contents}>
                {row.answer}
              </pre>
              <div style={qnaContent_container_bottom}>
                <ul
                  className="answerInfo"
                  style={{
                    listStyle: 'none',
                    marginTop: '10px',
                    display: 'flex',
                    flexDirection: 'row',
                    alignItems: 'center',
                  }}
                >
                  <li
                    className="answerer"
                    style={{
                      borderRight: '1px solid',
                      paddingRight: '10px',
                    }}
                  >
                    답변자: {row.answerAdmin}
                  </li>
                  <li className="answerDate" style={{ paddingLeft: '10px' }}>
                    답변일: {row.answerRegistrationDate.substring(0, 10)}
                  </li>
                </ul>
              </div>
            </div>
          </>
        )}
      </>
    );
  };

  return (
    <>
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          {QnAContent(getUser)}
          <button type="button" onClick={clickButton}>test1</button>
          <button type="button" onClick={clickButton}>test2</button>
        </header>
      </div>
    </>
  );
}

export default App;
